  Homesick.cart = {
    types: ["Homesick Candle"],

    outsideClose: function() {
      $(document).click(function(event) {
        if (
          !$(event.target).closest(
            ".ajax-cart, .icon-nav__item--cart"
          ).length
        ) {
          $("body").removeClass("ajax-cart-active");
        }
      });
    },

    openCart: function() {
      Homesick.navigation.reset();
      $("body").addClass("ajax-cart-active");
    },

    cartClick: function() {
      $(".icon-nav__item--cart a").on("click", function(e) {

          e.preventDefault();

          if ($(".ajax-cart-active").length) {
            $("body").removeClass("ajax-cart-active");
          } else {
            Homesick.cart.openCart();
          }

      });
    },

    spanTypes: function() {
      $(".ajax-cart-item__heading").each(function() {
        var types = Homesick.cart.types;
        var el = $(this);
        var text = el.text();

        for (var i = 0; i < types.length; i++) {
          if (text.indexOf(types[i])) {
            var state = text.split(types[i])[0];
            el.html(state + " <span>" + types[i] + "</span>");
            break;
          }
        }
      });
    },

    checkoutClick: function() {
      $(".button--ajax-checkout").on("click", function(e) {
        e.preventDefault();

        if ($("#gift-note").val().length) {
          var note = $("#gift-note").val();
          CartJS.setNote(note, {});
        }

        window.location = "/checkout";
      });
    },

    additionalClick: function() {
      $(".additional_checkout_buttons button").on("click", function() {
        if ($("#gift-note").val().length) {
          var note = $("#gift-note").val();
          CartJS.setNote(note, {});
        }
      });
    },

    noteFocus: function() {
      $(".ajax-cart__note-text").focus(function() {
        $(".ajax-cart__note").css("border-color", "#1b1f1f");
      });

      $(".ajax-cart__note-text").blur(function() {
        $(".ajax-cart__note").removeAttr("style");
      });
    },

    minicartChangeQty: function() {
      $(document).on('change', '.ajax-cart-item__quantity-selector', function(){
        $addToCartForm = $(".ajax-cart form");
        $.ajax({
          url: '/cart/update.js',
          dataType: 'json',
          type: 'post',
          data: $addToCartForm.serialize(),
          success: function(itemData) {
            Homesick.AjaxifyCart.updateHeaderTemplate();
          },
          error: function(XMLHttpRequest) {

          }
        });
      });

      $(document).on('click', '.ajax-cart-item__remove', function(){
        console.log("ss");
        $eleParent = $(this).parents('.ajax-cart-item');
        $eleParent.find('.ajax-cart-item__quantity-selector').val(0);
        $eleParent.find('.ajax-cart-item__quantity-selector').trigger('change');
        $eleParent.hide();
      });
    },

    init: function() {
      // this.spanTypes()
      this.cartClick();
      this.outsideClose();
      this.checkoutClick();
      this.additionalClick();
      this.noteFocus();
      this.minicartChangeQty();
    }
  };

