  Homesick.home = {
    initFixedHeader: function() {
      $(window).on("scroll", function() {
        if ($('.home-heros').length) {
          var pos = $(window).scrollTop();
          var min =
            $(".home-heros").offset().top +
            $(".home-heros").innerHeight();

          if (pos >= min) {
            $(".home-index__search").addClass(
              "home-index__search--fixed"
            );
          } else {
            $(".home-index__search").removeClass(
              "home-index__search--fixed"
            );
          }
        }
      });
    },

    initHideSeek: function() {
      $("#home-search").hideseek();

      $("#home-search").on("_after", function() {
        var hasText = $("#home-search").val().length;

        if (hasText) {
          $(
            ".new-grid-item--candle, .new-grid-item--product, .recommendations"
          ).hide();
        } else {
          $('li[style*="display: list-item"]').removeAttr("style");
          $('.recommendations').show();
          //$('.new-grid-item--candle, .new-grid-item--product').attr('style', '')
        }
      });
    },

    initSlider: function() {
      $(".home-heros").slick({
        arrows: false,
        dots: true,
        autoplay: true,
        fade: true,
        speed: 500,
        cssEase: "linear",
        autoplaySpeed: 5000
      });
    },

    initProductsSlider: function(){
      // featured products
      $('.products_widget-products').slick({
        dots: false,
        infinite: true,
        speed: 300,
        slidesToShow: 4,
        slidesToScroll: 4,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
          // You can unslick at a given breakpoint now by adding:
          // settings: "unslick"
          // instead of a settings object
        ]
      });
    },
    initCollectionSlider: function(){
      // featured products
      $('.collection_products').slick({
        dots: false,
        infinite: true,
        speed: 300,
        slidesToShow: 6,
        slidesToScroll: 1,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
          // You can unslick at a given breakpoint now by adding:
          // settings: "unslick"
          // instead of a settings object
        ]
      });
    },

    smoothSearch: function() {
      $(".icon-nav__item--search a").on("click", function(e) {
        e.preventDefault();

        $("html, body").animate(
          {
            scrollTop: $("#index").offset().top
          },
          800
        );

        $("#home-search").focus();
      });
    },

    searchCheck: function() {
      if (window.location.href.indexOf("#index") > 0) {
        $("#home-search").focus();
      }
    },

    searchFocus: function() {
      $("#home-search").focus(function() {
        $(".home-index__search").addClass(
          "home-index__search--active"
        );
      });

      $("#home-search").blur(function() {
        $(".home-index__search").removeClass(
          "home-index__search--active"
        );
      });
    },

    indexToggle: function() {
      $(".index-toggle__button").on("click", function(e) {
        e.preventDefault();
        $(".index-toggle__button").removeClass(
          "index-toggle__button--active"
        );
        $(this).addClass("index-toggle__button--active");

        if ($(this).hasClass("index-toggle__product")) {
          $(".new-grid-item--candle").removeClass(
            "new-grid-item--active"
          );
          $(".new-grid-item--product").addClass(
            "new-grid-item--active"
          );
        } else {
          $(".new-grid-item--candle").addClass(
            "new-grid-item--active"
          );
          $(".new-grid-item--product").removeClass(
            "new-grid-item--active"
          );
        }
      });
    },

    pressHover: function() {
      $('.home-press_logos img').on('mouseover click', function(event){
        var index = $(this).data('index');

        $('.home-press_logos .item').removeClass('active');
        $(this).parent().addClass('active');

        $('.home-press_quotations .item').removeClass('active');
        $('.home-press_quotations .item[data-index="' + index + '"]').addClass('active');
      });
    },

    pressMobileSlider: function() {
      $('.home-press_m-quotations').slick({
        dots: false,
        infinite: true,
        speed: 300,
        slidesToShow: 1,
        slidesToScroll: 1,
        adaptiveHeight: true
      });
    },

    init: function() {
      // this.initSlider();
      this.initProductsSlider();
      this.initCollectionSlider();
      this.smoothSearch();
      this.initHideSeek();
      this.searchCheck();
      this.searchFocus();
      this.initFixedHeader();
      this.indexToggle();
      this.pressHover();
      this.pressMobileSlider();
    }
