  Homesick.navigation = {
      stopPropagation: function() {
          $(
              ".nav__anchor-parent, .nav__dropdown, .hamburger, .nav"
          ).on("click", function(e) {
              e.stopPropagation();
          });
      },

      closeOnClickOutside: function() {
          $("html").click(function() {
              Homesick.navigation.reset();
          });
      },

      hamburgerClick: function() {
          $(".hamburger").on("click", function() {
              $("body").toggleClass("nav-active");
          });
      },

      navItemClick: function() {
          $(".nav__item--has-dropdown").on("mouseover", function(event) {
              event.preventDefault();

              if(!$(this).hasClass('nav__item--last')) {
                  var left = parseInt($(this).offset().left);
                  var self_w = $(this).find('.nav__dropdown').width();
                  var left_r = left+ self_w;
                  var window_w = $(window).width();
                  var window_w_r = window_w - 50;
                  if(window_w < 1065) {
                      window_w_r = window_w;
                  }
                  var offset_left = 0;
                  if(left_r > window_w_r) {
                      offset_left = 0 - (left_r - window_w_r);
                  }

                  if(offset_left < 0) {
                      $(this).find('.nav__dropdown').css('margin-left', offset_left + 'px');
                  }
              }

              $(".nav__anchor-parent--open").removeClass(
                  "nav__anchor-parent--open"
              );
              $(".nav__dropdown--active").removeClass(
                  "nav__dropdown--active"
              );

              $(this).find('.nav__anchor-parent').addClass("nav__anchor-parent--open");

              $(this)
                  .find(".nav__dropdown")
                  .addClass("nav__dropdown--active");

              if (Homesick.size.size < 40) {
                  $(this)
                      .find(".nav__dropdown")
                      .slideDown();
              }
          })
          .on('mouseleave', function(event) {
              event.preventDefault();

              $(this).find('.nav__dropdown').css('margin-left', '0px');

              $(".nav__anchor-parent--open").removeClass(
                  "nav__anchor-parent--open"
              );
              $(".nav__dropdown--active").removeClass(
                  "nav__dropdown--active"
              );

              $(this).find('.nav__anchor-parent').removeClass("nav__anchor-parent--open");

              $(this)
                  .find(".nav__dropdown")
                  .removeClass("nav__dropdown--active");

              if (Homesick.size.size < 40) {
                  $(this)
                      .find(".nav__dropdown")
                      .slideUp();
              }
          });
      },

      reset: function() {
          $(".nav-active").removeClass("nav-active");
          $(".nav__anchor-parent").removeClass("nav__anchor-parent--open");
          $(".nav__dropdown")
              .removeClass("nav__dropdown--active");
      },

      resizeListener: function() {
          $(window).on(
              "resize",
              debounce(function() {
                  if (
                      (Homesick.size.size > 39 && Homesick.size.prev_size < 40) ||
                      (Homesick.size.size < 40 && Homesick.size.prev_size > 39)
                  ) {
                      Homesick.navigation.reset();
                  }
              }, 250)
          );
      },

      hoverLink: function() {
          $('.nav__item--of-dropdown').on('mouseover', function() {
              var $parent = $(this).parents('.nav__item');
              if($parent.find('.nav__item--of-dropdown_img').length > 0) {
                  var img = $(this).data('image');
                  var $imgObj = $parent.find('.nav__item--of-dropdown_img');
                  $imgObj.css('background-image', 'url(' + img + ')');
              }
          })
          .on('mouseleave', function(event) {
              var $parent = $(this).parents('.nav__item');
              if($parent.find('.nav__item--of-dropdown_img').length > 0) {
                  var $imgObj = $parent.find('.nav__item--of-dropdown_img');
                  var img = $imgObj.data('original-image');
                  $imgObj.css('background-image', 'url(' + img + ')');
              }
          });
      },

      setListeners: function() {
          this.hamburgerClick();
          this.navItemClick();
          this.resizeListener();
          this.stopPropagation();
          this.closeOnClickOutside();
          this.hoverLink();
      },

      init: function() {
          this.setListeners();
      }
  };

