  Homesick.productVariants = {
      updatePrices: function(options) {
          var moneyFormat = options.money_format;
          var variant = options.variant;

          if (variant) {
              $(".product-details__price").html(
                  Shopify.formatMoney(variant.price, moneyFormat).replace(
                      "$ ",
                      "$"
                  )
              );
          }
      },

      addButtonStates: function(options, id) {
          var variant = options.variant;

          if (variant) {
              if (variant.available) {
                  $(".product-details__add-to-cart")
                      .removeClass("disabled")
                      .prop("disabled", false);
                  if (variant["inventory_quantity"] > 0) {
                      $(".product-details__add-to-cart-text").html(
                          "Add To Cart"
                      );
                  } else {
                      $(".product-details__add-to-cart-text").html(
                          "Add To Cart"
                      );
                  }
              } else {
                  $(".product-details__add-to-cart-text").html(
                      "Out Of Stock"
                  );
                  $(".product-details__add-to-cart")
                      .addClass("disabled")
                      .prop("disabled", true);
              }
          } else {
              $(".product-details__add-to-cart")
                  .addClass("disabled")
                  .prop("disabled", true);
              $(".product-details__add-to-cart-text").html("Unavailable");
          }
      },

      setVaraintId: function(options) {
          var id = options["variant"]["id"];
          $(".product-details__add-to-cart").attr("data-id", id);
      },

      fullSelect: function() {
          if ($(".selector-wrapper").length === 1) {
              $(".selector-wrapper").css("width", "100%");
          }
      },

      selectCallback: function(options) {
          this.updatePrices(options);
          this.addButtonStates(options, options["variant"]["id"]);
          this.setVaraintId(options);
          this.fullSelect();
      }
  };
  Homesick.product = {
    stickyStart: 80,
    stickyEnd: null,
    currentPosition: null,

    initSlider: function() {
      $(".product-mobile-header__slider").slick({
        arrows: false,
        dots: true,
        autoplay: false
      });
    },

    retrieveStickyEnd: function() {
      this.stickyEnd =
        $(".product-images").innerHeight() -
        $(".product-top__right").innerHeight() +
        this.stickyStart +
        1;
    },

    resizeListener: function() {
      $(window).on(
        "resize",
        debounce(function() {
          if (Homesick.size.size < 40) {
            Homesick.product.resetSticky();
          }

          Homesick.product.retrieveStickyEnd();
        }, 250)
      );
    },

    resetSticky: function() {
      $(".product-top__right")
        .removeClass("product-top__right--fixed")
        .removeAttr("style");
    },

    stickyListener: function() {
      $(window).on("scroll", function() {
        Homesick.product.retrieveStickyEnd();
        if (Homesick.size.size > 39) {
          var pos = $(window).scrollTop();
          var start = Homesick.product.stickyStart;
          var end = Homesick.product.stickyEnd;

          if (pos > start && pos < end) {
            $(".product-top__right")
              .addClass("product-top__right--fixed")
              .removeAttr("style");
          } else if (pos >= end) {
            $(".product-top__right")
              .css("position", "absolute")
              .css("top", end)
              .css("right", 0);
          } else {
            Homesick.product.resetSticky();
          }
        } else {
          Homesick.product.resetSticky();
        }
      });
    },

    addToCartClick: function() {
      $("#AddToCart").on("click", function(e) {
          e.preventDefault();
        var id = Number($(this).attr("data-id"));

        if (Homesick.size.size > 39) {
          CartJS.addItem(
            id,
            1,
            {},
            {
              success: function(data, textStatus, jqXHR) {
                Homesick.cart.openCart();
                console.log(CartJS.getCart);
              },
              error: function(jqXHR, textStatus, errorThrown) {
                console.log("Error: " + errorThrown + "!");
              }
            }
          );
        }
      });
    },

    descToggles: function() {
      $(".description-section__header").on("click", function() {
        var hasClass = $(this)
          .parent()
          .hasClass("description-section--active");

        $(".description-section--active").removeClass(
          "description-section--active"
        );
        $(".description-section__body").slideUp();

        if (!hasClass) {
          $(this)
            .parent()
            .addClass("description-section--active");
          $(this)
            .next()
            .slideDown();
        }
      });
    },

    setListeners: function() {
      this.stickyListener();
      this.resizeListener();
      // this.addToCartClick()
    },

    relatedCheck: function() {
      if (!$(".related-product--standard").length) {
        $(".related-products").hide();
      }
    },

    productImgSlider: function() {
      $(".product-images_slides").slick({
        arrows: false,
        adaptiveHeight: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        asNavFor: '.slider-nav'
      });
      $('.slider-nav').slick({
        slidesToShow: 5,
        slidesToScroll: 1,
        asNavFor: '.product-images_slides',
        dots: true,
        arrows: false,
        focusOnSelect: true
      });
    },

    initProductsSlider: function(){
      // featured products
      $('.products_widget-products').slick({
        dots: false,
        infinite: true,
        speed: 300,
        slidesToShow: 4,
        slidesToScroll: 4,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
          // You can unslick at a given breakpoint now by adding:
          // settings: "unslick"
          // instead of a settings object
        ]
      });
    },

    init: function() {
      this.initSlider();
      // this.retrieveStickyEnd();
      // this.setListeners();
      this.initProductsSlider();
      this.descToggles();
      this.relatedCheck();
      this.productImgSlider();
    }
  };

